import { createGlobalStyle } from "styled-components";
import {theme} from "./Theme";

export const GlobalStyle = createGlobalStyle`
*{
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}
a {
    text-decoration: none;
}
ul{
    list-style: none;
}
body{
    background-color: ${theme.colors.backgroundColor};
}
`;
