import styled from "styled-components";
import {font} from "../../styles/Common";
import {theme} from "../../styles/Theme";


type NavPropsType = {
    itemsMenu: Array<{ title: string, href: string }>,
    width: string
}


export const Nav = (props: NavPropsType) => {
    return (
        <StyledNav style={{width: props.width}}>
            <ul>
                {
                    props.itemsMenu.map((item, index) => {
                        return <ItemMenu key={index}><Link href={item.href}>{item.title}</Link></ItemMenu>
                    })
                }
            </ul>
        </StyledNav>
    )
}

const StyledNav = styled.nav`

    ul {
        width: 100%;
        display: flex;
        justify-content: space-around;
        gap: 2%;
    }
`;

const ItemMenu = styled.li`
    &:hover{
        border-bottom: 1.5px solid ${theme.colors.fontColor};
        transform: translateY(0) scale(105%);
    }
`;

const Link = styled.a`
    ${font({family: "\"Montserrat\", sans-serif", weight: 500, Fmax: 18, Fmin: 14, color: `${theme.colors.fontColor}`})};
    white-space: nowrap;


`;
