import * as React from 'react';
import styled from 'styled-components';
import {font} from '../../styles/Common';
import {theme} from '../../styles/Theme';
import {Icon} from '../icon/Icon';
import {useState} from 'react';

type AnswerType = {
    id:number
    title:string
    text:string
}

type QuestionItemPropsType = {
    question:string
    answer?:string
    answerobject?:Array<AnswerType>
};
export const QuestionItem = (props: QuestionItemPropsType) => {

    const[showAnswer, setShowAnswer] = useState(false);
    return (
        <StyledQuestionItem>
            <Title>{props.question}</Title>
            <Button type={'button'} onClick={()=>{setShowAnswer(!showAnswer)}}><Icon iconId={'plus'} width={'30px'} heigth={'30px'} viewBox={'0 0 30 30'}/></Button>
            {showAnswer && (<><Text>{props.answer}</Text> <div>{props.answerobject.map(el=>{ return(<Text>{el.title}</Text>)})}</div></>)}
        </StyledQuestionItem>
    );
};

const StyledQuestionItem = styled.div`
    display: flex;
    flex-wrap: wrap;
`;
const Title = styled.h4`
    width: 90%;
    ${font({family: '"Montserrat", sans-serif', weight: 500, Fmax: 23, Fmin: 15, color: `${theme.colors.fontColor}`})};
    padding-bottom: 20px;
`;
const Button = styled.button`
    width: 10%;
    background:none;
    border: none;
    cursor: pointer;

`;
const Text = styled.p`
    width: 90%;
    padding: 15px;
    border: 1px solid ${theme.colors.fontColor};
    border-width:0 0 0 1px ;
    ${font({family: '"Montserrat", sans-serif', weight: 500, Fmax: 20, Fmin: 15, color: `${theme.colors.colorGray}`})};
`;
