import * as React from 'react';
import styled from 'styled-components';
import {font} from '../../styles/Common';
import {theme} from '../../styles/Theme';
import {Icon} from '../icon/Icon';
import {useState} from 'react';
import {FlexWrapper} from '../FlexWrapper';


type Obj = {
    id: number
    title: string
    text: string
}


type QuestionItemPropsType = {
    question: string
    answer?: string
    objecA?: Array<Obj>
};
export const QuestionItem = (props: QuestionItemPropsType) => {

    const [showAnswer, setShowAnswer] = useState(false);
    return (
        <StyledQuestionItem>
            <Title>{props.question}</Title>
            <Button type={'button'} onClick={() => {
                setShowAnswer(!showAnswer)
            }}><Icon iconId={'plus'} width={'30px'} heigth={'30px'} viewBox={'0 0 30 30'}/></Button>
            {showAnswer ? <StyledAnswer>
                {!props.answer ? <>{props.objecA?.map((e,index) => {
                    return (<FlexWrapper justify={'space-between'} gap={'2%'} wrap={'wrap'}><span>{index+1}</span>
                        <AnswerTitle>{e.title}</AnswerTitle><Text>{e.text}</Text></FlexWrapper>)
                })}</> : <Text>{props.answer}</Text>}
            </StyledAnswer> : null}


        </StyledQuestionItem>
    );
};

const StyledQuestionItem = styled.div`
    display: flex;
    flex-wrap: wrap;
`;
const Title = styled.h4`
    width: 90%;
    ${font({family: '"Montserrat", sans-serif', weight: 500, Fmax: 23, Fmin: 15, color: `${theme.colors.fontColor}`})};
    padding-bottom: 20px;
`;
const Button = styled.button`
    width: 10%;
    background: none;
    border: none;
    cursor: pointer;

`;
const StyledAnswer = styled.div`
    border: 1px solid ${theme.colors.fontColor};
    border-width: 0 0 0 1px;
    padding: 15px;
    width: 90%;
    ${FlexWrapper}{
        width: 100%;
    }
`;

const Text = styled.p`
    ${font({family: '"Montserrat", sans-serif', weight: 500, Fmax: 20, Fmin: 15, color: `${theme.colors.colorGray}`})};
`;

const AnswerTitle = styled.h5`
    ${font({
        family: '"Montserrat", sans-serif',
        weight: 500,
        Fmax: 20,
        Fmin: 15,
        color: `${theme.colors.colorGray}`
    })};
    width: 50%;
    border: 1px solid;

`;
